package in.codechef.problem;

import java.util.Scanner;

public class BoneAppetit {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		Scanner sc = new Scanner(System.in);
		int a = sc.nextInt();
		int b = sc.nextInt();
		int x = sc.nextInt();
		int y = sc.nextInt();

		System.out.println(a * x + b * y);
		sc.close();
	}

}
